import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import {
    AppBar,
    Toolbar,
    Typography,
    Button,
    Grid,
    IconButton,
    Hidden,
    SwipeableDrawer,
} from '@material-ui/core'
import './NavBar.css'
import { 
    isAuthenticated,
    modifiedAuthProvider as authProvider
} from '../Admin/Providers/TokenAuthProvider'
import withWidth, { isWidthUp } from '@material-ui/core/withWidth';
import MenuIcon from '@material-ui/icons/Menu';
import ArrowBackIcon from '@material-ui/icons/ArrowBack';

const NavBar = props => {
    const [openBar, setOpenBar] = useState(false)

    const handleBarToggle = () => {
        setOpenBar(!openBar)
    }

    const navItems = [
        {
            title : 'Schedule',
            to : '/schedule'
        },
    ]
    const logged = isAuthenticated()
    const onClick = () => {
        authProvider.logout()
        window.location.reload()
    }

    const items = navItems.map(navItem => (
        <Link to={navItem.to}>
            <Button>
                <Typography variant="p" className="">
                    {navItem.title}
                </Typography>
            </Button>
        </Link>
    ))
    return (
        <AppBar position="static">
            <Toolbar>
                {isWidthUp('sm', props.width) ? (
                    <Grid container>
                        <Grid item xs={8}>
                            <Typography variant="h4" component="h1">
                                <Link className="text-white" to="/">Motorcyclers Schedule</Link>
                            </Typography>
                        </Grid>
                        <Grid item container alignItems="center" justify="flex-end" xs={4}>
                            {items}
                            {logged ? (
                                <Button onClick={() => onClick()}>
                                    <Typography variant="p" className="text-white">
                                        Logout
                                    </Typography>
                                </Button>
                            ) : (
                                <Link to='/login'>
                                    <Button>
                                        <Typography variant="p" className="text-white">
                                            Login
                                        </Typography>
                                    </Button>
                                </Link>
                            )}
                        </Grid>
                    </Grid>
                ) : (
                    <Grid container>
                        <Grid item xs={2}>
                            <IconButton onClick={() => handleBarToggle()}>
                                <MenuIcon style={{color:'white'}}/>
                            </IconButton>
                        </Grid>
                        <Grid item xs={10} container alignItems='center' justify="flex-end">
                            <Typography variant="subtitle2" component="h1">
                                <Link className="text-white" to="/">Motorcyclers Schedule</Link>
                            </Typography>
                        </Grid>
                        <SwipeableDrawer open={openBar}>
                            <Grid container >
                                <Grid item container xs="6" justify="flex-start">
                                    <IconButton onClick={() => handleBarToggle()}>
                                        <ArrowBackIcon/>
                                    </IconButton>
                                </Grid>
                                {items}
                            </Grid>
                        </SwipeableDrawer>
                    </Grid>
                )}
            </Toolbar>
        </AppBar>
    )
}
export default withWidth()(NavBar)
