import tokenAuthProvider from 'ra-data-django-rest-framework'
import './apiUrl'

const authProvider = tokenAuthProvider({obtainAuthTokenUrl : apiUrl + '/api-token-auth/'})

export const modifiedAuthProvider = {
    ...authProvider,
    login : async ({ username, password }) => {
        console.log(opts)
        const request = new Request(opts.obtainAuthTokenUrl, {
            method: 'POST',
            body: JSON.stringify({ username, password }),
            headers: new Headers({ 'Content-Type': 'application/json' }),
        });
        const response = await fetch(request);
        if (response.ok) {
            localStorage.setItem('token', (await response.json()).token);
            localStorage.setItem('is_staff', (await response.json()).is_staff);
            return;
        }
        if (response.headers.get('content-type') !== 'application/json') {
            throw new Error(response.statusText);
        }
        const json = await response.json();
        const error = json.non_field_errors;
        throw new Error(error || response.statusText);
    },
    logout: () => {
        localStorage.removeItem('token');
        localStorage.removeItem('is_staff');
        return Promise.resolve();
    },
    getPermissions: () => {
        return localStorage.getItem('is_staff') ? Promise.resolve() : Promise.reject()
    },
}
