import React, {useState} from 'react';
import axios from 'axios'
import apiUrl from '../Admin/Providers/apiUrl'

const Schedule = () => {
    return (
        <table style={{margin : 'auto'}}>
            <tr>
                <td>
                    <TableItem pk={1}/>
                </td>
            </tr>
        </table>
    )
}

class TableItem extends React.Component {
    state = {
        motorcyclers : {}
    }

    componentDidMount() {
        axios.get(apiUrl + `/motorcyclers/${this.props.pk}/get/`)
        .then(response => {
            const motorcycler = response.data
            this.setState({
                motorcyclers : {
                    id : motorcycler.id,
                    max_amount : motorcycler.max_amount,
                    actual_amount : motorcycler.actual_amount,
                }
            })
        })
    }

    render() {
        const styles = {
            width : '100px',
            height : '100px',
            textAlign : 'center',
            backgroundColor : 'blue',
        }
        return (
            <div style={styles}>
                {this.state.motorcyclers.actual_amount}
            </div>
        )
    }
}

const colors = {
    selected : {
        backgroundColor : 'green'
    },
    notSelected : {
        backgroundColor : 'blue'
    },
    outOfMotorcyclers : {
        backgroundColor : 'red'
    }
}

export default Schedule
